#' Gene ID Conversion#'#' This function filters an SQLite dataframe based on a list of values and selects specific columns.#' @param list_ensembl A list containing the values to filter with.#' @param keyid The column in the dataframe to use for filtering.#' @param outputid A vector of column names to select.#' @return A dataframe containing the filtered and selected results.#' @exportcoversion_id_genes <- function(list_ensembl, keyid, outputid) {  library(dplyr)  library(dbplyr)  library(RSQLite)    table_name <- "geneslator"    # Path to the SQLite database within the package  sqlite_path <- system.file("extdata", "geneslator.sqlite", package = "geneslator.package")    # Connect to the SQLite database  conn <- dbConnect(SQLite(), sqlite_path)  on.exit(dbDisconnect(conn), add = TRUE)  # Close the connection at the end of the function    # Check the existence of the table  tables <- dbListTables(conn)  if (!table_name %in% tables) {    stop("Table ", table_name, " does not exist in the database.")  }    # Reference the table using dplyr/dbplyr  table <- tbl(conn, table_name)    # Filter the dataframe based on values present in list_ensembl  filtered_table <- table %>%    filter(!!sym(keyid) %in% list_ensembl) %>%    select(all_of(outputid)) %>%    collect()    return(filtered_table)}