#' Gene ID Conversion#'#' This function filters an SQLite dataframe based on a list of values and selects specific columns.#' @param list_ensembl A vector containing the values to filter with.#' @param keyid The column in the dataframe to use for filtering (e.g., "ENSEMBL").#' @param outputid A vector of column names to select.#' @return A dataframe containing the filtered and selected results.#' @exportconversion_id_genes <- function(list_ensembl, keyid, outputid) {  library(dplyr)  # Define the table name  table_name <- "geneslator"    # Get the path to the SQLite database included in the package  sqlite_path <- system.file("extdata", "geneslator.sqlite", package = "Geneslator")    # Check if the database file exists  if (sqlite_path == "") {    stop("The SQLite database file was not found. Ensure it is included in the package under inst/extdata/")  }    # Connect to the SQLite database  conn <- DBI::dbConnect(RSQLite::SQLite(), sqlite_path)  on.exit(DBI::dbDisconnect(conn), add = TRUE)  # Ensure the connection is closed after execution    # Check if the specified table exists in the database  tables <- DBI::dbListTables(conn)  if (!table_name %in% tables) {    stop("Table '", table_name, "' does not exist in the database.")  }    # Load the table using dbplyr  table <- dplyr::tbl(conn, table_name)    # Check if the keyid column exists in the database  db_columns <- colnames(table)  if (!(keyid %in% db_columns)) {    stop("Column '", keyid, "' not found in the database table.")  }    # Check if the requested output columns exist in the database  missing_cols <- setdiff(outputid, db_columns)  if (length(missing_cols) > 0) {    stop("The following columns do not exist in the database: ", paste(missing_cols, collapse = ", "))  }    # Apply filtering and select the requested columns  filtered_table <- table %>%    dplyr::filter(.data[[keyid]] %in% list_ensembl) %>%    dplyr::select(dplyr::all_of(outputid)) %>%    dplyr::collect()    return(filtered_table)}