#' Gene ID Conversion#'#' This function filters an SQLite dataframe based on a list of values and selects specific columns.#' @param list_ensembl A list containing the values to filter with.#' @param keyid The column in the dataframe to use for filtering.#' @param outputid A vector of column names to select.#' @return A dataframe containing the filtered and selected results.#' @exportconversion_id_genes <- function(list_ensembl, keyid, outputid) {  library(dplyr)  library(dbplyr)  library(RSQLite)    table_name <- "geneslator"    # Path to the SQLite database within the package  sqlite_path <- system.file("extdata", "geneslator.sqlite", package = "geneslator.package")    # Check if the SQLite file exists  if (!file.exists(sqlite_path)) {    stop("The SQLite database file does not exist: ", sqlite_path)  }    # Connect to the SQLite database  conn <- dbConnect(SQLite(), sqlite_path)  on.exit(dbDisconnect(conn), add = TRUE)  # Ensure connection is closed after function execution    # Check the existence of the table  tables <- dbListTables(conn)  if (!table_name %in% tables) {    stop("Table ", table_name, " does not exist in the database.")  }    # Reference the table using dplyr/dbplyr  table <- tbl(conn, table_name)    # Safely handle dynamic column names  keyid_sym <- rlang::sym(keyid)  outputid_syms <- rlang::syms(outputid)    # Filter the dataframe based on values present in list_ensembl  filtered_table <- table %>%    filter(!!keyid_sym %in% list_ensembl) %>%    select(!!!outputid_syms) %>%    collect() # Pull results into a local dataframe    return(filtered_table)}