#' Gene ID Conversion#'#' This function filters an SQLite dataframe based on a list of values and selects specific columns.#' @param list_ensembl A list containing the values to filter with.#' @param keyid The column in the dataframe to use for filtering.#' @param outputid A vector of column names to select.#' @return A dataframe containing the filtered and selected results.#' @exportconversion_id_genes<-function (list_ensembl, keyid, outputid) {  library(dplyr)  library(dbplyr)  library(RSQLite)  table_name <- "geneslator"  sqlite_path <- system.file("extdata", "geneslator.sqlite",                              package = "Geneslator")  if (!file.exists(sqlite_path)) {    stop("The SQLite database file does not exist: ", sqlite_path)  }  conn <- DBI::dbConnect(SQLite(), sqlite_path)  on.exit(dbDisconnect(conn), add = TRUE)  tables <- dbListTables(conn)  if (!table_name %in% tables) {    stop("Table ", table_name, " does not exist in the database.")  }  table <- tbl(conn, table_name)  keyid_sym <- rlang::sym(keyid)  outputid_syms <- rlang::syms(outputid)  filtered_table <- table %>% dplyr::filter(!!keyid_sym %in% list_ensembl) %>%     dplyr::select(!!!outputid_syms) %>% collect()  return(filtered_table)}